require 'pry'

describe("new post page") do

  it("posts form data to a feed page") do
    visit('/new_post')
    fill_in "img_title", with: "test portrait"
    fill_in "img_category", with: "portrait"
    fill_in "img_url", with: "data:image/jpeg;base64,"
    fill_in "img_description", with: "blah blah blah"
    click_on 'submit'
    visit('/feed')
    expect(page).to have_content("This is your feed!")
  end


  it("will have a form for making a new post") do
    visit('/new_post')
    fill_in "img_title", with: "test portrait"
    fill_in "img_category", with: "portrait"
    fill_in "img_url", with: "data:image/jpeg;base64,"
    fill_in "img_description", with: "blah blah blah"
    click_on 'submit'
    expect(page).to have_content("test portrait")
  end






end #describe


